// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;
pragma abicoder v2;  // Enable ABI coder v2

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract BlockStorage is Ownable, Pausable {
    struct HashRecord {
        uint8 c;
        uint32 m;
        uint8 t;
        uint8 v;
        bytes32 k;
        bytes s;
    }

    uint256 public constant BASE_REWARD = 10 ether;
    uint256 public constant BASE_REWARD_INTERVAL_S = 365 * 24 * 60 * 60;
    uint256 public constant TARGET_BLOCK_TIME_S = 1;
    uint256 public constant DEFAULT_DIFFICULTY = 100_000;
    uint256 public constant GENESIS_TS = 1694158682; // 2023-09-08T07:38:01.656Z
    string public constant GENESIS_TWEET = 'https://twitter.com/mrJackLevin/status/1700050845562515517';
    string public constant AUTHORS = '@mrJackLevin';

    // hash ID => hash record
    mapping(uint256 => bytes) public hashRecords;
    // address => idx => hash ID
    mapping(address => mapping(uint256 => uint256)) public hashIdsByAddress;
    // address => record counter
    mapping(address => uint256) public recordCount;
    // hash ID => account address
    mapping(uint256 => address) public addressesByHashId;
    // key => account address
    mapping(bytes32 => address) public addressesByKey;

    // params settable by the authority
    uint256 public variance = 20;
    uint256 public epochDurationSec = 600;
    uint256 public difficultyAdjustment = 2_000;

    uint256 public currentReward = BASE_REWARD;     // halves every 365 days
    uint256 public currentRewardTs = GENESIS_TS;    // is set every 365 days
    uint256 public currentHashId = 1;               // increments with each new Hash
    uint256 public currentLogId = 1;                // increments with each new HashLog
    uint256 public currentEpoch;                    // increments every epoch
    uint256 public currentEpochTs;                  // is set every epoch
    uint256 public difficulty;                      // is adjusted every epoch
    uint256 public epochHashCounter;                // resets every epoch

    constructor(uint256 _currentEpoch, uint256 _difficulty) {
        currentEpoch = _currentEpoch > 0 ? _currentEpoch : 1;
        difficulty = _difficulty > 0 ? _difficulty : DEFAULT_DIFFICULTY;
    }

    event NewHashLogShort(uint256 indexed logId, uint256 indexed epoch, uint256 indexed ts, uint256 count);
    event NewHashLog(uint256 indexed logId, uint256 indexed epoch, uint256 indexed ts, uint256 count, bytes32 hash);
    event NewHash(uint256 indexed hashId, uint256 indexed epoch, address indexed account, uint256 ts, bytes _bytes);
    event NewEpoch(uint256 indexed epoch, uint256 difficulty, uint256 ts);

    event BaseRewardSet(uint256 indexed epoch, uint256 reward, uint256 ts);

    event VarianceSet(uint256 indexed epoch, uint256 indexed variance);
    event EpochDurationSet(uint256 indexed epoch, uint256 indexed duration);
    event DifficultySet(uint256 indexed epoch, uint256 indexed difficulty);
    event DifficultyAdjustmentSet(uint256 indexed epoch, uint256 indexed adjustment);

    function setVariance(uint256 _variance) external onlyOwner {
        require(_variance > 0 && _variance < 100, 'X1: Illegal Variance');
        variance = _variance;
        emit VarianceSet(currentEpoch, _variance);
    }

    function setEpochDurationSec(uint256 _epochDurationSec) external onlyOwner {
        require(_epochDurationSec > 0, 'X1: Illegal Epoch Duration');
        epochDurationSec = _epochDurationSec;
        emit EpochDurationSet(currentEpoch, _epochDurationSec);
    }

    function setDifficulty(uint256 _difficulty) external onlyOwner {
        require(_difficulty > 0, 'X1: Illegal Difficulty');
        difficulty = _difficulty;
        emit DifficultySet(currentEpoch, _difficulty);
    }

    function setDifficultyAdjustment(uint256 _difficultyAdjustment) external onlyOwner {
        require(_difficultyAdjustment > 0 && _difficultyAdjustment < difficulty, 'X1: Illegal Difficulty Adjustment');
        difficultyAdjustment = _difficultyAdjustment;
        emit DifficultyAdjustmentSet(currentEpoch, _difficultyAdjustment);
    }

    function lockDifficulty() external onlyOwner {
        _pause();
    }

    function unlockDifficulty() external onlyOwner {
        _unpause();
    }

    /*
        @dev Internal utility for adjusting difficulty
    */
    function _adjustDifficulty() internal {
        uint256 targetHashCount = epochDurationSec / TARGET_BLOCK_TIME_S;
        uint256 lowerBound = targetHashCount * (100 - variance) / 100;
        uint256 upperBound = targetHashCount * (100 + variance) / 100;
        if (epochHashCounter < lowerBound && difficulty >= difficultyAdjustment && !paused()) {
            difficulty -= difficultyAdjustment;
        } else if (epochHashCounter > upperBound && !paused()) {
            difficulty += difficultyAdjustment;
        }
    }

    /*
        @dev Public utility for good housekeeping
    */
    function doFullHousekeeping() public {
        if (block.timestamp - currentEpochTs > epochDurationSec) {
            currentEpochTs = block.timestamp;
            currentEpoch++;
            _adjustDifficulty();
            epochHashCounter = 0;
            emit NewEpoch(currentEpoch, difficulty, currentEpochTs);
        }
        if (block.timestamp - currentRewardTs > BASE_REWARD_INTERVAL_S) {
            currentRewardTs = block.timestamp;
            currentReward /= 2;
            emit BaseRewardSet(currentEpoch, currentReward, currentRewardTs);
        }
    }

    /*
        @dev Public utility for good housekeeping
    */
    function doHousekeeping() public {
        if (block.timestamp - currentEpochTs > epochDurationSec) {
            currentEpochTs = block.timestamp;
            currentEpoch++;
            epochHashCounter = 0;
            emit NewEpoch(currentEpoch, difficulty, currentEpochTs);
        }
        if (block.timestamp - currentRewardTs > BASE_REWARD_INTERVAL_S) {
            currentRewardTs = block.timestamp;
            currentReward /= 2;
            emit BaseRewardSet(currentEpoch, currentReward, currentRewardTs);
        }
    }

    /*
        Internal
        @dev baseline method for storing records
    */
    function _storeRecord(address _address, uint256 _hashId, bytes memory _bytes) internal returns (bool) {
        if (addressesByHashId[_hashId] != address(0)) return false;
        ( , , , , bytes32 k, ) = decodeRecordBytes(_bytes);
        if (addressesByKey[k] != address(0)) return false;
        if (hashRecords[_hashId].length > 0) return false;

        hashRecords[_hashId] = _bytes;
        hashIdsByAddress[_address][++recordCount[_address]] = _hashId;
        addressesByHashId[_hashId] = _address;
        addressesByKey[k] = _address;
        return true;
    }

    /*
        Internal
        @dev baseline method for storing _new_ records
    */
    function _storeNewRecord(address _address, uint256 _hashId, bytes memory _bytes) internal {
        require(_storeRecord(_address, _hashId, _bytes), 'X1: Duplicate HashId or Key');
        emit NewHash(_hashId, currentEpoch, _address, block.timestamp, _bytes);
        epochHashCounter++;
        // this is done as the last step to avoid using new epoch values
        doFullHousekeeping();   // check / start new epoch if required
    }

    /*
        Public interface
        @dev Store a new record encoded as HashRecord struct
        @deprecated production code should use storeNewRecord(HashRecord calldata r)
    */
    function storeNewRecord(address _address, HashRecord calldata r) external {
        _storeNewRecord(_address, ++currentHashId, abi.encodePacked(r.c, r.m, r.t, r.v, r.k, r.s));
    }

    /*
        Public interface
        @dev Store a new record encoded as HashRecord struct
    */
    function storeNewRecord(HashRecord calldata r) external {
        _storeNewRecord(_msgSender(), ++currentHashId, abi.encodePacked(r.c, r.m, r.t, r.v, r.k, r.s));
    }

    /*
        Public interface
        @dev Store a new record encoded as separate parameters
        @deprecated production code should use
        storeNewRecordData(uint8 c, uint32 m, uint8 t, uint8 v, bytes32 k, bytes calldata s)
    */
    function storeNewRecordData(address _address, uint8 c, uint32 m, uint8 t, uint8 v, bytes32 k, bytes calldata s) external {
        _storeNewRecord(_address, ++currentHashId, abi.encodePacked(c, m, t, v, k, s));
    }

    /*
        Public interface
        @dev Store a new record encoded as separate parameters
    */
    function storeNewRecordData(uint8 c, uint32 m, uint8 t, uint8 v, bytes32 k, bytes calldata s) external {
        _storeNewRecord(_msgSender(), ++currentHashId, abi.encodePacked(c, m, t, v, k, s));
    }

    /*
        Public interface
        @dev Store a new record encoded as bytes
        @deprecated production code should use storeNewRecordBytes(bytes calldata _bytes)
    */
    function storeNewRecordBytes(address _address, bytes calldata _bytes) external {
        _storeNewRecord(_address, ++currentHashId, _bytes);
    }

    /*
        Public interface
        @dev Store a new record encoded as bytes
    */
    function storeNewRecordBytes(bytes calldata _bytes) external {
        _storeNewRecord(_msgSender(), ++currentHashId, _bytes);
    }

    /*
        Public interface
        TODO: do we need bulk upload for new records?
        @dev Store a set of new records encoded as bytes
        @deprecated use individual storeNewRecord/storeNewRecordBytes/storeNewRecordData calls
    */
    function bulkStoreNewRecordsInc(address[] calldata addresses, bytes[] calldata bb) external {
        require(addresses.length == bb.length, 'X1: Array Length Mismatch');
        require(addresses.length > 0, 'X1: Empty Array');
        for (uint i = 0; i < bb.length; i++) {
            bool ok = _storeRecord(addresses[i], ++currentHashId, bb[i]);
            if (ok) {
                emit NewHash(currentHashId, currentEpoch, addresses[i], block.timestamp, bb[i]);
                epochHashCounter++;
            }
        }
        doFullHousekeeping();   // check / start new epoch if required
    }

    /*
        Admin interface
        @dev Store a set of records encoded as bytes. Used for backfilling.
        @deprecated will be used with _onlyOwner modifier
    */
    function bulkStoreRecordBytes(address[] calldata addresses, bytes[] calldata bb) external {
        require(addresses.length == bb.length, 'X1: Array Length Mismatch');
        require(addresses.length > 0, 'X1: Empty Array');
        for (uint i = 0; i < bb.length; i++) {
            _storeRecord(addresses[i], ++currentHashId, bb[i]);
        }
    }

    function logStoreRecordBytes(bytes[] calldata bb) external {
        require(bb.length > 0, 'X1: Record bytes empty');
        epochHashCounter += bb.length;
        doHousekeeping();
        emit NewHashLogShort(currentLogId++, currentEpoch, block.timestamp, bb.length);
    }

    function logStoreRecords(uint16 count, bytes calldata blob) external {
        require(count > 0, 'X1: Record count cannot be zero');
        require(blob.length > 0, 'X1: Record bytes empty');
        epochHashCounter += count;
        doHousekeeping();
        emit NewHashLog(currentLogId++, currentEpoch, block.timestamp, count, keccak256(blob));
    }

    /*
        @deprecated Do not use in production
    */
    function getBytesLen(address _address, uint256 _index) external view returns (uint len) {
        bytes memory _data = hashRecords[hashIdsByAddress[_address][_index]];
        assembly {
            len := mload(_data)
        }
    }

    /*
        Public interface
        @dev decode a record stored as bytes into discrete params
    */
    function decodeRecordBytes(bytes memory _data)
        public
        pure
        returns (uint8 c, uint32 m, uint8 t, uint8 v, bytes32 k, bytes memory s)
    {
        uint len;
        assembly {
            len := mload(_data)
            c := mload(add(_data, 1))
            m := mload(add(_data, 5))
            t := mload(add(_data, 6))
            v := mload(add(_data, 7))
            k := mload(add(_data, 39))
        }
        s = new bytes(len - 39);
        assembly {
            // Skip the first 32 bytes reserved for the length of the memory array
            let offset := add(s, 0x20)
            let count := sub(len, 39)
            for { let i := 0} lt(i, count) { i := add(i, 1) } {
                mstore8(add(offset, i), mload(add(_data, add(40, i))))
            }
        }
    }

    /*
        Public interface
        @dev decode a record referenced by a HashId into discrete params
    */
    function decodeRecordBytes(uint256 _hashId)
        public
        view
        returns (uint8 c, uint32 m, uint8 t, uint8 v, bytes32 k, bytes memory s)
    {
        return decodeRecordBytes(hashRecords[_hashId]);
    }

    /*
        Public interface
        @dev decode a record referenced by an address and index into discrete params
    */
    function decodeRecordBytes(address _address, uint256 _idx)
        public
        view
        returns (uint8 c, uint32 m, uint8 t, uint8 v, bytes32 k, bytes memory s)
    {
        return decodeRecordBytes(hashIdsByAddress[_address][_idx]);
    }

    /*
        Public interface
        @dev helper to get all HashIds for an address
    */
    function getHashIdsByAddress(address _address) public view returns (uint256[] memory ids) {
        uint256 count = recordCount[_address];
        ids = new uint256[](count);
        for (uint i = 0; i < count; i++) {
            ids[i] = hashIdsByAddress[_address][i+1];
        }
    }

    /*
        Public interface
        @dev helper to get all HashRecords for an address
    */
    function getRecordsByAddress(address _address) public view returns (HashRecord[] memory recs) {
        uint256 count = recordCount[_address];
        recs = new HashRecord[](count);
        for (uint i = 0; i < count; i++) {
            uint256 hashId = hashIdsByAddress[_address][i+1];
            (recs[i].c, recs[i].m, recs[i].t, recs[i].v, recs[i].k, recs[i].s) = decodeRecordBytes(hashId);
        }
    }
}
